import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import ButtonBase from '@material-ui/core/ButtonBase';
import Grid from '@material-ui/core/Grid';
import NavBar from './NavBar';
import ListDividers from './ListDividers';

const useStyles = makeStyles({
    card: {
        minWidth: 275,
        margin: 30,
    }
});
export default function SpeechPage(props) {
	const classes = useStyles();



    return (
        <div>
            <NavBar />
            <Card className={classes.card}>
                <CardContent>
                    {props.history.location.state.name}
                </CardContent>
            </Card>
        </div>
	);

	return (
		<div>
			<NavBar Show={hideMenu} SetSpeechCards={setSpeechCards} />
			<Grid container spacing={0}
				direction="row"
				justify="flex-start"
				alignItems="stretch">
				{show && <Grid item xs={2}>
					<ListDividers />
				</Grid>}
				{show && <Grid item xs={10}>
					<div id="cards">
						<Grid container spacing={2}
							direction="row"
							justify="flex-start"
							alignItems="baseline">
							{speechCards.map(speech => (
								<Grid key={speech.id} item xs={3}>
									<Link style={{ textDecoration: 'none' }} to={{
										pathname: `/speech/${speech.id}`,
										state: { name: speech.name, content: speech.content, show: true }
									}}>
										<Card className={classes.paper}>
											<CardActions>
												<ButtonBase>
													<CardContent>
														<b>{speech.name}</b><br />
														<p className={classes.p} align="left">{speech.content.substring(0, 255) + '...'}</p>
													</CardContent>
												</ButtonBase>
											</CardActions>
										</Card>
									</Link>
								</Grid>
							))}
						</Grid>
					</div>
				</Grid>}
				{!show && <div id="cards">
					<Grid container spacing={2}
						direction="row"
						justify="flex-start"
						alignItems="baseline">
						{speechCards.map(speech => (
							<Grid key={speech.id} item xs={3}>
								<Link style={{ textDecoration: 'none' }} to={{
									pathname: `/speech/${speech.id}`,
									state: { name: speech.name, content: speech.content, show: false }
								}}>
									<Card className={classes.paper}>
										<CardActions>
											<ButtonBase>
												<CardContent>
													<b>{speech.name}</b><br />
													<p className={classes.p} align="left">{speech.content.substring(0, 255) + '...'}</p>
												</CardContent>
											</ButtonBase>
										</CardActions>
									</Card>
								</Link>
							</Grid>
						))}
					</Grid>
				</div>}
			</Grid>
		</div>
	);
}
