import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import ButtonBase from '@material-ui/core/ButtonBase';
import Grid from '@material-ui/core/Grid';
import NavBar from './NavBar';
import ListDividers from './ListDividers';

const useStyles = makeStyles(theme => ({
	root: {
		flexGrow: 1,
	},
	cards: {
		height: '89vh'
	},
	paper: {
		textAlign: 'center',
		color: theme.palette.text.secondary,
		margin: '15px'
	},
	p: {
		textIndent: '40px'
	},
	ButtonBase: {
		width: '100%',
		height: '100%',
	},
	CardContent: {
		padding: '15px',
		width: '100%',
		height: '100%',
	},
}));


let rows = createData(speeches);

export default function HomePage() {
	const classes = useStyles();

	const [show, setShow] = useState(false);

	const [speechCards, setSpeechCards] = useState(rows);

	const hideMenu = () => {
		return (show) ? setShow(false) : setShow(true);
	}

	const searchSpeeches = (speeches) => {
		return setSpeechCards(speeches);
	}


	return (
		<div>
			<NavBar Show={hideMenu} SetSpeechCards={setSpeechCards}/>
			<Grid container spacing={0}
				direction="row"
				justify="flex-start"
				alignItems="stretch">
				{show && <Grid item xs={2}>
					<ListDividers />
				</Grid>}
				{show && <Grid item xs={10}>
					<div id="cards">
						<Grid container spacing={2}
							direction="row"
							justify="flex-start"
							alignItems="baseline">
							{speechCards.map(speech => (
								<Grid key={speech.id} item xs={3}>
									<Card className={classes.paper}>
										<ButtonBase>
											<CardContent>
												<b>{speech.name}</b><br />
												<p className={classes.p} align="left">{speech.content.substring(0, 255) + '...'}</p>
											</CardContent>
											<CardActions>
												<Link to={`/speech/${speech.id}`}>{speech.name}'s Page</Link>
											</CardActions>
										</ButtonBase>
									</Card>
								</Grid>
							))}
						</Grid>
					</div>
				</Grid>}
				{!show && <div id="cards">
					<Grid container spacing={2}
							direction="row"
							justify="flex-start"
							alignItems="baseline">
						{speeches.map(speech => (
							<Grid key={speech.id} item xs={3}>
									<Card className={classes.paper}>
										<ButtonBase>
											<CardContent>
												<b>{speech.name}</b><br />
												<p className={classes.p} align="left">{speech.content.substring(0, 255) + '...'}</p>
											</CardContent>
											<CardActions>
											</CardActions>
										</ButtonBase>
									</Card>
							</Grid>
						))}
					</Grid>
				</div>}
				</Grid>
		</div>
	);
}
