import React from 'react';
import IconButton from '@material-ui/core/IconButton';
import { makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { Link } from 'react-router-dom';
import { extractScalePropsFromProps } from 'react-vis/dist/utils/scales-utils';

const useStyles = makeStyles(theme => ({
	root: {
		flexGrow: 1,
	},
	menuButton: {
		marginRight: theme.spacing(2),
	},
}));

export default function FilterSpeeches(props) {
	const classes = useStyles();
	const [anchorEl, setAnchorEl] = React.useState(null);

	function handleClick(event) {
		setAnchorEl(event.currentTarget);
	}

	function handleClose(name) {
		props.Sort(name);
		setAnchorEl(null);
	}

	return (
		<div>
			<IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu" onClick={handleClick}>
				<MenuIcon />
			</IconButton>
			<Menu
				id="simple-menu"
				anchorEl={anchorEl}
				keepMounted
				open={Boolean(anchorEl)}
				onClose={handleClose}
			>
				<MenuItem onClick={handleClose("name")}>Name</MenuItem>
				<MenuItem onClick={handleClose("created")}>Date Created</MenuItem>
				<MenuItem onClick={handleClose("updated")}>Last Updated</MenuItem>
			</Menu>
		</div>
	);
}
