import React,  { useState } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import HomePage from './components/HomePage';
import Settings from './Pages/Settings';
import SpeechPage from './components/SpeechPage'
import CssBaseline from '@material-ui/core/CssBaseline';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';
import lightBlue from '@material-ui/core/colors/lightBlue';
import red from '@material-ui/core/colors/red';

export default function App() {

	const [isThemeLight, setThemeLight] = useState(true);

	const Mode = (Mode) => {
		return (Mode === true) ? setThemeLight(true) : setThemeLight(false);
	}

	const themeLight = createMuiTheme({
		palette: {
			primary: lightBlue,
			error: red,
		},
	});

	const themeDark = createMuiTheme({
		palette: {
			primary: lightBlue,
			error: red,
			type: 'dark'
		},
	});

	return (
		<Router>
			<div>
				<ThemeProvider theme={isThemeLight ? themeLight : themeDark}>
					<CssBaseline />
					<Route exact path='/' component={HomePage} />
					<Route exact path='/Settings' component={() => <Settings isAuthed={true} />} />
					<Route exact path="/speech/:SpeechId" component={SpeechPage} />
				</ThemeProvider>
			</div>
		</Router>
	);
}
